```typescript
import * as React from'react';

interface Props {
  name: string;
  age: number;
  avatarUrl: string;
}

const UserProfile: React.FC<Props> = ({
  name,
  age,
  avatarUrl,
}) => (
  <div>
    <h1>{name}</h1>
    <p>Age: {age}</p>
    <img 
      src={avatarUrl} 
      alt="Avatar"
      onClick={() => alert(`Name: ${name}, Age: ${age}`)}
    />
  </div>
);

export default UserProfile;
```
